import { all, fork, put, delay, takeLatest } from 'redux-saga/effects';
import axios from 'axios';
import { LOG_IN_SUCCESS, LOG_IN_FAILURE, LOG_OUT_SUCCESS,
  LOG_OUT_FAILURE, LOG_IN_REQUEST, LOG_OUT_REQUEST,
  SIGN_UP_REQUEST, SIGN_UP_SUCCESS, SIGN_UP_FAILURE, UNFOLLOW_REQUEST, FOLLOW_REQUEST, FOLLOW_SUCCESS, FOLLOW_FAILURE, UNFOLLOW_SUCCESS,
} from '../reducers/user';

function logInAPI(data) {
  return axios.post('/api/login', data); 
}

function* logIn(action) {
  try {
    console.log('saga start');
    // const result = yield call(logInAPI, action.data);
    yield delay(1000);
    console.log('log');
    yield put({
      type: LOG_IN_SUCCESS,
      data: action.data,
    });
  } catch (err) {
    console.error(err);
    yield put({
      type: LOG_IN_FAILURE,
      error: err.response.data,
    });
  }
}

function logOutAPI() {
  return axios.post('/api/logout');
}
function* logOut() {
  try {
    yield delay(1000);
    //  const result = yield call(logOutAPI);
    yield put({
      type: LOG_OUT_SUCCESS,
      //     data: result.data,
    });
  } catch (err) {
    yield put({
      type: LOG_OUT_FAILURE,
      error: err.response.data,
    });
  }
}

function signUpAPI() {
  return axios.post('/api/signup');
}
function* signUp() {
  try {
    yield delay(1000);

    //  const result = yield call(logOutAPI);
    yield put({
      type: SIGN_UP_SUCCESS,
      //     data: result.data,
    });
  } catch (err) {
    yield put({
      type: SIGN_UP_FAILURE,
      error: err.response.data,
    });
  }
}


function followAPI() {
  return axios.post('/api/follow');
}
function* follow() {
  try {
    yield delay(1000);

    //  const result = yield call(logOutAPI);
    yield put({
      type: FOLLOW_SUCCESS
      //     data: result.data,
    });
  } catch (err) {
    yield put({
      type: FOLLOW_FAILURE,
      error: err.response.data,
    });
  }
}


function unfollowAPI() {
  return axios.post('/api/unfollow');
}
function* unfollow() {
  try {
    yield delay(1000);

    //  const result = yield call(logOutAPI);
    yield put({
      type: UNFOLLOW_SUCCESS
      //     data: result.data,
    });
  } catch (err) {
    yield put({
      type: SIGN_UP_FAILURE,
      error: err.response.data,
    });
  }
}




function* watchLogin() {
  console.log('watchLogin');
  yield takeLatest(LOG_IN_REQUEST, logIn);
}

function* watchLogOut() {
  yield takeLatest(LOG_OUT_REQUEST, logOut);
}

function* watchSignUp() {
  yield takeLatest(SIGN_UP_REQUEST, signUp);
}


function* watchFollow() {
  yield takeLatest(FOLLOW_REQUEST, follow);
}

function* watchUnfollow() {
  yield takeLatest(UNFOLLOW_REQUEST, unfollow);
}


export default function* userSaga() {
  yield all([
    fork(watchLogin),
    fork(watchLogOut),
    fork(watchSignUp),
    fork(watchFollow),
    fork(watchUnfollow),
  ]);
}
